import React, { useState } from "react";
import "./Current.css";
import Currentdate from "./Currentdate"; 


const [celsius, setCelsius] = useState("currentTemp");
const [farenheit, setFarenheit] = useState("hiddenTemp");
function convertUnit(event) {
  event.preventDefault();
  if (celsius === "currentTemp") {
    setCelsius("hiddenTemp");
    setFarenheit("currentTemp");
  }
  else {
    setCelsius("currentTemp");
    setFarenheit("hiddenTemp");
  }
}

export default function Current(props) {
  return (
    <div className="current-form">
      <hr />
      <div className="row currentInfo">
        <div className="col-6">
          <p className="currentCity">{props.data.city}</p>
          <div className="currentTime"><Currentdate date={props.data.date}/></div>
          <p className="currentDescription text-capitalize">{props.data.description}</p>
        </div>
        <div className="col-2">
          <img
            className="currentPicture"
            src={props.data.icon}
            alt={props.data.description}
          />
        </div>
        <div className="col-4 currentTemp">
          <span className="TempC"> {props.data.temperature} </span>
          <span className="TempC">°C</span>
          <span className="secondTemp">→<a href="/" onClick={convertUnit}>°F</a></span>
          <p className="humidity">Humidity {props.data.humidity} %</p>
          <p className="wind">Wind {props.data.wind } m/c</p>
        </div>
        <div className="col-4 hiddenTemp">
          <span className="TempF"> {props.data.temperature} </span>
          <span className="TempF">°F</span>
          <span className="secondTemp">→<a href="/" onClick={convertUnit}>°C</a></span>
          <p className="humidityF">Humidity {props.data.humidity} %</p>
          <p className="windF">Wind {props.data.wind} m/c</p>
        </div>
      </div>
    </div>
  );
}
